import React from 'react'
import { Link } from 'react-router-dom';
import PostList from './PostList'
import '../css/Dashboard.css';
import { fetchPosts } from '../action';
import { connect } from 'react-redux';
import { toastr } from 'react-redux-toastr';

class Dashboard extends React.Component {
    state = { data: undefined }

    componentWillMount() {
        if (localStorage.getItem('IsLogedIn')) {
            this.props.fetchPosts((res) => {
                this.setState({ data: res.data })
            });
        }
    }

    onLogout = () => {
        localStorage.removeItem('token');
        localStorage.setItem("IsLogedIn", false);
        toastr.info('Logout Successfully');

    }

    strip_html_tags(str) {
        if ((str === null) || (str === ''))
            return false;
        else
            str = str.toString();
        return str.replace(/<[^>]*>/g, '');
    }

    trimByWord(sentence) {
        var result = sentence;
        var resultArray = result.split(' ');
        if (resultArray.length > 10) {
            resultArray = resultArray.slice(0, 10);
            result = resultArray.join(' ') + '…';
        }
        return result;
    }

    renderbutton=(uid)=>{
        const userid = localStorage.getItem('userid');
        if(uid==userid){
            <button classname='ui pro'></button>
        }
    }

    renderlist = () => {


        if (this.state.data !== undefined) {
            const _this = this;
            return this.state.data.map((post, index) => {
                const description = _this.strip_html_tags(post.excerpt.rendered)
                return (
                    <div className='item' key={index}>
                        {_this.renderbutton(post.author)}
                        <i className='large middle aligned icon user' />
                        <div className='content'>
                            <div className='header' >
                                {post.title.rendered}
                            </div>
                            <div className='description'>
                                {this.trimByWord(description)}
                            </div>  
                        </div>

                    </div>
                );
            })
        }
        else {
            return (
                <div>
                    <h2 style={{textAlign:'center'}}>Loading...</h2>
                </div>
            );
        }

    }

    renderDashBoard() {
        const name = localStorage.getItem("Username")
        const temp = (name.charAt(0)).toUpperCase();

        if (localStorage.getItem("IsLogedIn")) {
            return (
                <div>
                    <div className=" ui secondary pointing menu" >
                        <div className="right menu" >

                            <Link to="/EditProfile">
                                <div className="avatar-circle" >
                                    <span className="initials" ><div className='character' >{temp}</div></span>
                                </div></Link>
                            <Link to="/CreatePost" className="item" >Create Post</Link>
                            <Link to="/" onClick={this.onLogout} className="item" >LogOut</Link>

                        </div>
                    </div>
                    <div>
                        <div className='ui celled list' >
                            {this.renderlist()}
                        </div>
                    </div>
                </div>
            );
        }
        else {
            return (
                <div>
                    <div className=" ui secondary pointing menu" >
                        <div className="right menu" >
                            <Link to="/" className="item" >Login</Link>
                        </div>
                    </div>
                    <h2 style={{ textAlign: 'center' }}>Please Login First</h2>
                </div>

            );
        }
    }
    render() {
        return (
            <div>
                {this.renderDashBoard()}
            </div>

        )
    }
}

export default connect(null, { fetchPosts })(Dashboard)
