import PostForm from './PostForm';
import { createPost } from '../action';
import { connect } from 'react-redux';
import _ from 'lodash'


class EditPost extends React.Component {

    componentDidMount() {
    }

    onSubmit = (formvalues) => {

        // this.props.editPost(formvalues,(res)=>{
        //     console.log(res);
        // });
        // history.push('/Dashboard');

    }
    strip_html_tags(str) {
        if ((str === null) || (str === ''))
            return '';
        else
            str = str.toString();
        return str.replace(/<[^>]*>/g, '');
    }

    initiateForm=()=>{
        const id = this.props.match.params.id;
        return this.props.post.map((post,index) => {
            if ((post.id).toString() === id) {
                console.log(post,'true');
                console.log(post.title.rendered);
                console.log(this.strip_html_tags9);
                console.log(post.status);
                const initialvalues={
                    "title":post.title.rendered,
                    "content":post.content.rendered,
                    "status":post.status
                }
                return (
                    <div key={index}>
                        <PostForm
                            initialValues={_.pick(post.content.rendered, 'title', 'content', 'status')}
                            onSubmit={this.onSubmit}
                        />
                    </div>
                )
            }    
            else{
                return(
                    <div></div>
                );
            }    
        })
    }

    render() {
        return(
            <div>
                {this.initiateForm()}
            </div>
        )


    }
}

const mapStateToProps = (state) => {

    return { post: state.posts }
}

export default connect(mapStateToProps, { createPost })(EditPost);
