import React from 'react'
import { Field, reduxForm } from 'redux-form';
import '../css/Form.css'

class Form extends React.Component {

    renderError({ error, touched }) {
        if (touched && error) {
            return (
                <div className='error message'>
                    <div className="errormessage">{error}</div>
                </div>
            );
        }
    }

    renderInput = ({ input, type,label, meta ,maxLength}) => {
        return (

            <div className='field'>
                <label>{label}</label>
                <input {...input} type={type} maxLength={maxLength}/>
                {this.renderError(meta)}
            </div>
        )
    }

    onSubmit = (formValues) => {
        this.props.onSubmit(formValues);
    }

    render() {
        return (
            <form className="ui form error" onSubmit={this.props.handleSubmit(this.onSubmit)}>
                <Field name="firstname" component={this.renderInput} label="First Name" maxLength="30"/>
                <Field name="lastname" component={this.renderInput} label="Last Name" maxLength="30"/>
                <Field name="email" component={this.renderInput} label="E-mail" maxLength="50"/>
                <Field name="username" component={this.renderInput} label="UserName" maxLength="20" />
                <Field name="password" component={this.renderInput} label="Password" maxLength="20" />
                <Field name="ConfirmPassword" component={this.renderInput} label="ConfirmPassword" maxLength="20" />
                <button className="ui button primary">Submit</button>

            </form>
        );
    }
}

const validate = (formValues) => {
    const errors = {}
    if (!formValues.firstname) {
        errors.firstname = 'you must Enter Title'
    }
    if (!formValues.lastname) {
        errors.lastname = 'you must Enter Description'
    }
    if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(formValues.email)) {
        errors.email = 'Enter Valid Email'
    }
    if (!/[^a-zA-Z0-9 ]/i.test(formValues.username)) {
        errors.username = 'Only Alfanumeric value will aceepted'
    }
    if (!formValues.password) {
        errors.password = 'you must Enter Password'
    }
    if (!formValues.ConfirmPassword) {
        errors.ConfirmPassword = 'Please Re-Enter Password';
    } else if (formValues.ConfirmPassword !== formValues.password) {
        errors.ConfirmPassword = 'Password mismatched';
    }

    return errors;
}

const wrapedform = reduxForm({
    form: 'Form',
    validate
})(Form);


export default wrapedform;
