import React from 'react'
import PostForm from './PostForm';
import { updatePost } from '../action';
import { connect } from 'react-redux';
import _ from 'lodash'
import {toastr} from 'react-redux-toastr';
import history from '../History';



class EditPost extends React.Component {

    componentDidMount() {
    }

    onSubmit = (formvalues) => {
        const {id}=this.props.match.params;
        this.props.updatePost(id,formvalues,(res)=>{
            if(res.status===200){
                toastr.success('Done',"Post updated successfully");
                history.pushState('/Dashboard');
            }
            else{

            }
        });

    }
    strip_html_tags(str) {
        if ((str === null) || (str === ''))
            return '';
        else
            str = str.toString();
        return str.replace(/<[^>]*>/g, '');
    }

    initiateForm = () => {
        const id = this.props.match.params.id;
        return this.props.post.map((post, index) => {
            if ((post.id).toString() === id) {
                const content = this.strip_html_tags(post.content.rendered);
                const initialvalues = {
                    "title": post.title.rendered,
                    "content": content,
                    "status": post.status
                };
                return (
                    <div key={index}>
                        <PostForm
                            initialValues={_.pick(initialvalues, 'title', 'content', 'status')}
                            onSubmit={this.onSubmit}
                        />
                    </div>
                )
            }
            else {
                return (
                    <div></div>
                );
            }
        })
    }

    render() {
        if (this.props.post !== '') {
            return (
                <div>
                    {this.initiateForm()}
                </div>
            );
        }
        else {
            return (
                <div>
                    <h2>
                        Please Try Again...
                    </h2>
                </div>
            )
        }
    }
}

const mapStateToProps = (state) => {

    return { post: state.posts }
}

export default connect(mapStateToProps, {updatePost})(EditPost);
