import React from 'react'
import { Field, reduxForm } from 'redux-form'

const validate = values => {
    const errors = {}
    if (!values.title) {
        errors.title = 'Required'
    } else if (values.title.length > 15) {
        errors.title = 'Must be 15 characters or less'
    }
    if (!values.description) {
        errors.description = 'Required'
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.description)) {
        errors.description = 'Invalid description address'
    }
    if (!values.age) {
        errors.age = 'Required'
    } else if (isNaN(Number(values.age))) {
        errors.age = 'Must be a number'
    } else if (Number(values.age) < 18) {
        errors.age = 'Sorry, you must be at least 18 years old'
    }
    return errors
}

const renderInput = ({ input, label, type, meta: { touched, error, warning } }) => (
    <div>
        <label>{label}</label>
        <div>
            <input {...input} placeholder={label} type={type} />
            {touched && (error && <span>{error}</span>)}
        </div>
    </div>
)

const renderField = ({ input, label, type, meta: { touched, error, warning } }) => (
    <div>
        <label>{label}</label>
        <div>
            <textarea {...input} placeholder={label} type={type} />
            {touched && (error && <span>{error}</span>)}
        </div>
    </div>
)



const SyncValidationForm = props => {
    const { handleSubmit, pristine, reset, submitting } = props
    return (
        <div>
            <h2>Create </h2>    
            <form onSubmit={handleSubmit}>
                <div className='ui form error'>
                    <div className='field'>
                        <Field
                            name="title"
                            type="text"
                            component={renderInput}
                            label="Title"
                        />
                    </div>
                    <div className='field'>
                        <Field name="description" component={renderField} label="Description" />
                    </div>
                    <div className='field'>
                        <Field name="age" type="number" component={renderInput} label="Age" />
                    </div>

                    <div className='field'>
                        <button className='ui secondary button' type="button" disabled={pristine || submitting} onClick={reset}>
                            Clear Values
                    </button>
                        <button className='ui primary button' type="submit" disabled={submitting}>
                            Submit
                    </button>
                    </div>
                </div>
            </form>
        </div>
    )
}

export default reduxForm({
    form: 'syncValidation', // a unique identifier for this form
    validate, // <--- validation function given to redux-form
})(SyncValidationForm)
