import React from 'react'
import { Link } from 'react-router-dom';
import PostList from './PostList'
import '../css/Dashboard.css';
import { fetchPosts } from '../action';
import { connect } from 'react-redux';
import { toastr } from 'react-redux-toastr';

class Dashboard extends React.Component {
    state = { flag: false }

    componentWillMount() {
        this.props.fetchPosts();
        this.setState({
            flag: localStorage.getItem("IsLogedIn"),
            name: localStorage.getItem("Username")
        })

    }

    onLogout = () => {
        localStorage.removeItem("IsLogedIn");
        localStorage.removeItem('token');
        this.setState({ flag: false });
        toastr.info('Logout Successfully');

    }

    strip_html_tags(str) {
        if ((str === null) || (str === ''))
            return false;
        else
            str = str.toString();
        return str.replace(/<[^>]*>/g, '');
    }

    trimByWord(sentence) {
        var result = sentence;
        var resultArray = result.split(' ');
        if(resultArray.length > 10){
        resultArray = resultArray.slice(0, 10);
        result = resultArray.join(' ') + '…';
        }
        return result;
        }

    renderlist() {
        console.log(this.props.posts);
        const { posts } = this.props;
        const _this =this;
        if (posts!== undefined) {
            return posts.map((post, index) => { 
                const description = _this.strip_html_tags(post.excerpt.rendered)
                return (
                    <div className='item' key={index}>
                        <i className='large middle aligned icon user' />
                        <div className='content'>
                            <div className='header' >  
                            {post.title.rendered}
                            </div>
                            <div className='description'>
                                {this.description}
                            </div>
                        </div>
                    </div>
                );
            })
        }
        else{
            return(
                <div>
                    <h2>Loading...</h2>
                </div>
            );
        }

    }

    renderDashBoard() {
        const temp = (this.state.name.charAt(0)).toUpperCase();

        if (this.state.flag) {
            return (
                <div>
                    <div className=" ui secondary pointing menu" >
                        <div className="right menu" >

                            <Link to="/EditProfile">
                                <div className="avatar-circle" >
                                    <span className="initials" ><div className='character' >{temp}</div></span>
                                </div></Link>

                            <Link to="/CreatePost" className="item" >Create Post</Link>
                            <Link to="/" onClick={this.onLogout} className="item" >LogOut</Link>

                        </div>
                    </div>
                    <div>
                        <div className='ui celled list' >
                            {this.renderlist()}
                        </div>
                    </div>
                </div>
            );
        }
        else {
            return (
                <div>
                    <div className=" ui secondary pointing menu" >
                        <div className="right menu" >
                            <Link to="/" className="item" >Login</Link>
                        </div>
                    </div>
                    <h2 style={{ textAlign: 'center' }}>Please Login First</h2>
                </div>

            );
        }
    }
    render() {
        return (
            <div>
                {this.renderDashBoard()}
            </div>

        )
    }
}

const mapStateToProps = (state) => {

    return { posts: state.users[0] }
}

export default connect(mapStateToProps, { fetchPosts })(Dashboard)
