import React from 'react'
import {Link} from 'react-router-dom';
import PostList from './PostList'

class Dashboard extends React.Component {
    state={flag:false}

    componentWillMount(){
        console.log(localStorage.getItem("IsLogedIn"),'willmount');
        this.setState({flag:localStorage.getItem("IsLogedIn")})
    }
    onLogout=()=>{
        localStorage.removeItem("IsLogedIn");
        localStorage.removeItem('Userid');
    }

    renderDashBoard(){
        console.log()
        if(this.state.flag){
        return (
            <div>
                <div className=" ui secondary pointing menu" >
                    <div className="right menu" >
                        <Link to="/CreatePost" className="item" >Create Post</Link>
                        <Link to="/" onClick={this.onLogout} className="item" >LogOut</Link>

                    </div>
                </div>
                <div>
                    <PostList />
                </div>
            </div>
        );
        }
        else{
            return(
                <div className='header'>
                    <h2>Please Login First</h2>
                </div>
            );
        }
    }
    render() {
        return(
            <div>
            {this.renderDashBoard()}
            </div>

        )
    }
}

export default Dashboard;
