import React from 'react'
import {Link} from 'react-router-dom';
import PostList from './PostList'
import '../css/Dashboard.css';

class Dashboard extends React.Component {
    state={flag:false}

    componentWillMount(){
        console.log(localStorage.getItem("IsLogedIn"),'willmount');
        this.setState({
            flag:localStorage.getItem("IsLogedIn")
        })
    }
    onLogout=()=>{
        localStorage.removeItem("IsLogedIn");
        localStorage.removeItem('Userid');
        this.setState({flag:false})
    }

    renderDashBoard(){
        console.log(this.state.flag);
        if(this.state.flag){
        return (
            <div>
                <div className=" ui secondary pointing menu" >
                    <div className="right menu" >
                        <div className='avatar' >
                        <Link to="/EditProfile" className="item" >Profile</Link>
                        </div>

                        <div className="avatar-circle" >
                        <span className="initials" >JD</span>
                        </div>

                        <Link to="/CreatePost" className="item" >Create Post</Link>
                        <Link to="/" onClick={this.onLogout} className="item" >LogOut</Link>

                    </div>
                </div>
                <div>
                    <PostList />
                </div>
            </div>
        );
        }
        else{
            return(
                <div>
                <div className=" ui secondary pointing menu" >
                    <div className="right menu" >
                        <Link to="/" className="item" >Login</Link>                    
                    </div>
                </div>
                <h2 style={{textAlign:'center'}}>Please Login First</h2>
            </div>

            );
        }
    }
    render() {
        return(
            <div>
            {this.renderDashBoard()}
            </div>

        )
    }
}

export default Dashboard;
