import React from 'react'
import { Field, reduxForm } from 'redux-form';

class CreatePost extends React.Component {

    //const { handleSubmit, pristine, reset, submitting } = props
    renderField = ({ input, label, type, meta: { touched, error, warning } }) => (
        <div>
            <label>{label}</label>
            <div>
                <textarea {...input} placeholder={label} type={type} />
                {touched && (error && <span className='errormessage' >{error}</span>)}
            </div>
        </div>
    )

    renderInput = ({ input, label, type, meta: { touched, error } }) => (
        <div>
            <label>{label}</label>
            <div>
                <input {...input} placeholder={label} type={type} />
                {touched && (error && <span className='errormessage' >{error}</span>)}
            </div>
        </div>
    )

    onSubmit=(values)=> {
        this.props.onSubmit(values);
    }

    render() {
        return (
            <div>
                <h2 style={{ textAlign: 'center' }}>Create Post</h2>
                <form onSubmit={this.props.handleSubmit(this.onSubmit)}>
                    <div className='ui form error'>
                        <div className='field'>
                            <Field
                                name="title"
                                type="text"
                                component={this.renderInput}
                                label="Title"
                            />
                        </div>
                        <div className='field'>
                            <Field name="description" component={this.renderField} label="Description" />
                        </div>
                        <div className='field'> 
                            <Field name="favoriteColor" component="select" >
                            <option value="">Choose here</option>
                                <option value="publish" >publish</option>
                                <option value="future">future</option>
                                <option value="draft">draft</option>
                                <option value="pending">pending</option>
                                <option value="private">private</option>
                            </Field>
                        </div>

                        <div className='field'>
                            <button className='ui secondary button' type="button" disabled={this.props.pristine || this.props.submitting} onClick={this.props.reset}>
                                Clear Values
                    </button>
                            <button className='ui primary button' type="submit" disabled={this.props.submitting}>
                                Submit
                    </button>
                        </div>
                    </div>
                </form>
            </div>
        )
    }
}

const validate = values => {
    const errors = {}
    if (!values.title) {
        errors.title = 'Required'
    } else if (values.title.length > 15) {
        errors.title = 'Must be 15 characters or less'
    }
    if (!values.description) {
        errors.description = 'Required'
    }
    // else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.description)) {
    //     errors.description = 'Invalid description address'
    // }
    if (!values.age) {
        errors.age = 'Required'
    } else if (isNaN(Number(values.age))) {
        errors.age = 'Must be a number'
    } else if (Number(values.age) < 18) {
        errors.age = 'Sorry, you must be at least 18 years old'
    }
    return errors
}



export default reduxForm({
    form: 'syncValidation', // a unique identifier for this form
    validate, // <--- validation function given to redux-form
})(CreatePost)
