import React from 'react'
import {Link} from 'react-router-dom';
import PostList from './PostList'
import '../css/Dashboard.css';
import {fetchPosts} from '../action';
import {connect} from 'react-redux';

class Dashboard extends React.Component {
    state={flag:false}

    componentWillMount(){

        this.setState({
            flag:localStorage.getItem("IsLogedIn"),
            name:localStorage.getItem("Username")
        })
        this.props.fetchPosts((responsearray)=>{
            responsearray.data.map((res)=>{

            })
        });
    }
    onLogout=()=>{
        localStorage.removeItem("IsLogedIn");
        localStorage.removeItem('token');
        this.setState({flag:false})
    }

    renderlist(){
        const {posts}=this.props;
        return posts.map((post,index)=>{
            return(
                <div className='item' key={index}>
                        <i className='large middle aligned icon camera' />
                        <div className='content'>
                            {/* <Link to={`/streams/${stream.id}`} className="header"> */}
                                {post.title.rendered}
                            {/* </Link> */}
                            <div className='description'>
                                {post.excerpt.rendered}
                            </div>
                        </div>
                    </div> 
            );

        })

    }

    renderDashBoard(){
        const temp =(this.state.name.charAt(0)).toUpperCase();

        if(this.state.flag){
        return (
            <div>
                <div className=" ui secondary pointing menu" >
                    <div className="right menu" >

                        <Link to="/EditProfile">
                        <div className="avatar-circle" >
                        <span className="initials" ><div className='character' >{temp}</div></span>
                        </div></Link>

                        <Link to="/CreatePost" className="item" >Create Post</Link>
                        <Link to="/" onClick={this.onLogout} className="item" >LogOut</Link>

                    </div>
                </div>
                <div>
                <div>
                {renderlist()}
            </div>
                </div>
            </div>
        );
        }
        else{
            return(
                <div>
                <div className=" ui secondary pointing menu" >
                    <div className="right menu" >
                        <Link to="/" className="item" >Login</Link>                    
                    </div>
                </div>
                <h2 style={{textAlign:'center'}}>Please Login First</h2>
            </div>

            );
        }
    }
    render() {
        return(
            <div>
            {this.renderDashBoard()}
            </div>

        )
    }
}

const mapStateToProps = (state) => {
    return { posts: state.users[0]}
}

export default connect(mapStateToProps,{fetchPosts})(Dashboard)
