import React from 'react'
import PostForm from './PostForm';
import { createPost } from '../action';
import { connect } from 'react-redux';
import _ from 'lodash'


class EditPost extends React.Component {

    componentDidMount() {
    }

    onSubmit = (formvalues) => {

        // this.props.editPost(formvalues,(res)=>{
        //     console.log(res);
        // });
        // history.push('/Dashboard');

    }
    strip_html_tags(str) {
        if ((str === null) || (str === ''))
            return '';
        else
            str = str.toString();
        return str.replace(/<[^>]*>/g, '');
    }

    initiateForm = () => {
        const id = this.props.match.params.id;
        return this.props.post.map((post, index) => {
            if ((post.id).toString() === id) {
                console.log(post, 'true');
                const content = this.strip_html_tags(post.content.rendered);
                const initialvalues = {
                    "title": post.title.rendered,
                    "content": content,
                    "status": post.status
                };
                return (
                    <div key={index}>
                        <PostForm
                            initialValues={_.pick(initialvalues, 'title', 'content', 'status')}
                            onSubmit={this.onSubmit}
                        />
                    </div>
                )
            }
            else {
                return (
                    <div>Please Retry...</div>
                );
            }
        })
    }

    render() {
        if (this.props.post !== '') {
            return (
                <div>
                    {this.initiateForm()}
                </div>
            );
            }    
        else{
            return(
                <div>
                    <h2>
                        Please Try Again...
                    </h2>
                </div>
            )
        }    


        }
    }

    const mapStateToProps = (state) => {

        return { post: state.posts }
    }

    export default connect(mapStateToProps, { createPost })(EditPost);
